BDS2024009,3456789020,Fatima,Ahmed,78 Station Road,,Birmingham,B21 8QW,7800,AUR-2024-009,2024-06-28
BDS2024010,,Susan,Taylor,45 Green Lane,,Stoke-on-Trent,ST6 2AB,13500,AUR-2024-010,2024-06-28`;

return [
  {
    json: {
      file_found: true,
      file_name: 'sample-auriga-data.csv',
      file_size: sampleData.length,
      processing_timestamp: new Date().toISOString(),
      csv_content: sampleData
    }
  }
];

// Parse CSV data manually (since csvToJson node may not be available)
const csvContent = $input.first().json.csv_content;
const lines = csvContent.trim().split('\n');
const headers = lines[0].split(',');
const records = [];

// Parse each line into JSON object
for (let i = 1; i < lines.length; i++) {
  const values = lines[i].split(',');
  const record = {};

  for (let j = 0; j < headers.length; j++) {
    record[headers[j]] = values[j] || '';
  }

  records.push(record);
}

console.log(`Parsed ${records.length} customer records from CSV`);

// Return each record as separate item for processing
return records.map(record => ({ json: record }));

// BDS Validation and Transformation Logic
const validationErrors = [];
const transformedData = [];
const processingTimestamp = new Date().toISOString();

// Helper functions
function calculateDiscountPercentage(income) {
  if (income <= 6000) return 90;   // Extreme poverty
  if (income <= 10000) return 80;  // Very low income
  if (income <= 15000) return 70;  // Low income
  if (income <= 18000) return 50;  // Moderate low income
  if (income <= 21000) return 40;  // Below average
  if (income <= 23492) return 30;  // BDS threshold
  return 10; // Minimum discount
}

function calculateRenewalDate() {
  const renewalDate = new Date();
  renewalDate.setFullYear(renewalDate.getFullYear() + 1);
  return renewalDate.toISOString();
}

function calculateMonthlyPayment(discount_percentage) {
  const standardMonthlyBill = 39.00;
  const discountedAmount = standardMonthlyBill * (discount_percentage / 100);
  const finalPayment = standardMonthlyBill - discountedAmount;
  return Math.max(3.90, finalPayment); // Minimum ¬£3.90 per month
}

// Process each customer record
for (const item of $input.all()) {
  const customer = item.json;
  const errors = [];

  // Validation rules
  if (!customer.customer_ref || customer.customer_ref.trim() === '') {
    errors.push('Missing customer reference');
  }

  if (!customer.account_number || customer.account_number.trim() === '') {
    errors.push('Missing account number');
  }

  if (!customer.household_income) {
    errors.push('Missing household income');
  }

  // Income validation
  const income = parseFloat(customer.household_income);
  if (isNaN(income) || income < 0) {
    errors.push('Invalid household income format');
  } else if (income > 23492) {
    errors.push(`Income ¬£${income} exceeds BDS threshold (¬£23,492)`);
  }

  // Account number validation (10 digits)
  if (customer.account_number && !/^\d{10}$/.test(customer.account_number.toString())) {
    errors.push('Invalid account number format (must be 10 digits)');
  }

  if (errors.length > 0) {
    validationErrors.push({
      customer_ref: customer.customer_ref || 'UNKNOWN',
      account_number: customer.account_number || 'UNKNOWN',
      errors: errors,
      original_data: customer,
      processing_timestamp: processingTimestamp
    });
  } else {
    // Transform valid data
    const discount_percentage = calculateDiscountPercentage(income);
    const monthly_payment = calculateMonthlyPayment(discount_percentage);

    const krakenCustomer = {
      account_number: customer.account_number,
      customer_reference: customer.customer_ref,
      first_name: customer.first_name,
      last_name: customer.last_name,
      address_line_1: customer.address_1 || '',
      address_line_2: customer.address_2 || '',
      city: customer.city || '',
      postcode: customer.postcode || '',
      household_income: income,
      discount_percentage: discount_percentage,
      monthly_payment: monthly_payment,
      annual_savings: (39.00 * 12) - (monthly_payment * 12),
      application_date: processingTimestamp,
      status: 'pending_approval',
      scheme_type: 'big_difference_scheme',
      renewal_date: calculateRenewalDate(),
      auriga_ref: customer.auriga_reference,
      priority_processing: income < 8000 ? 'high' : 'standard'
    };

    transformedData.push(krakenCustomer);
  }
}

// Calculate statistics
const totalProcessed = $input.all().length;
const validCount = transformedData.length;
const errorCount = validationErrors.length;
const successRate = totalProcessed > 0 ? ((validCount / totalProcessed) * 100).toFixed(2) : 0;
const totalAnnualSavings = transformedData.reduce((sum, c) => sum + c.annual_savings, 0);
const highPriorityCount = transformedData.filter(c => c.priority_processing === 'high').length;

console.log(`Processing complete: ${validCount}/${totalProcessed} records valid (${successRate}%)`);
console.log(`Total annual savings: ¬£${totalAnnualSavings.toFixed(2)}`);
console.log(`High priority customers: ${highPriorityCount}`);

return [
  {
    json: {
      valid_customers: transformedData,
      validation_errors: validationErrors,
      processing_summary: {
        total_processed: totalProcessed,
        valid_count: validCount,
        error_count: errorCount,
        success_rate_percentage: successRate,
        processing_timestamp: processingTimestamp,
        high_priority_customers: highPriorityCount,
        total_annual_savings: totalAnnualSavings
      }
    }
  }
];

// Simulate database insert operation
// In production, this would be actual database calls
const validCustomers = $input.first().json.valid_customers || [];
const processingData = $input.first().json.processing_summary;

if (validCustomers.length > 0) {
