  // Log some sample records being "inserted"
  validCustomers.slice(0, 3).forEach((customer, index) => {
    console.log(`Record ${index + 1}: ${customer.first_name} ${customer.last_name} - ${customer.discount_percentage}% discount (¬£${customer.monthly_payment}/month)`);
  });
} else {
