// Simulate error logging
const validationErrors = $input.first().json.validation_errors || [];

if (validationErrors.length > 0) {
  console.log(`Simulating error log insert for ${validationErrors.length} failed records`);

  validationErrors.forEach((error, index) => {
    console.log(`Error ${index + 1}: Customer ${error.customer_ref} - ${error.errors.join(', ')}`);
  });
} else {
  console.log('No validation errors to log');
}

return [
