// 1. Get the raw output from the AI, which might include markdown backticks
const rawOutput = $('AI Agent').item.json.output;

// 2. Use a regular expression to find and extract the JSON object within the text.
// This looks for the first '{' to the very last '}' and everything in between.
const jsonMatch = rawOutput.match(/\{[\s\S]*\}/);

// 3. Check if we found a valid JSON string
if (jsonMatch && jsonMatch[0]) {
  // 4. If we found it, parse only the extracted JSON part
  return JSON.parse(jsonMatch[0]);
} else {
  // 5. If no JSON was found, throw an error to stop the workflow
  throw new Error("Could not find a valid JSON object in the AI's output.");
}
